mapper.select:
SELECT MAX(code) FROM department

	WHERE parent_code = #{parentCode,jdbcType=VARCHAR}


	WHERE parent_code IS NULL

SELECT d.id,d.`name`,d.remark,d.`code`,d.parent_code AS parentCode,o.`name` AS operatorName FROM department AS d,operator AS o WHERE d.operator_id = d.id AND d.operator_id = #{operatorId,jdbcType=VARCHAR}
AND d.`name` LIKE CONCAT('%',#{dsDTO.name,jdbcType=VARCAHR},'%')
AND o.`name` LIKE CONCAT('%',#{dsDTO.operatorName,jdbcType=VARCAHR},'%')
